dispach_once dispach_once 函数中的代码块只会被执行一次，而且还是线程安全的
@interface Person : NSObject + (instancetype)sharePerson; @end  @implementation Person + (instancetype)allocWithZone:(struct _NSZone *)zone { static Person *instance = nil; // 一次性执行 static dispatch_once_t onceToken; dispatch_once(&amp;onceToken, ^{ instance = [super allocWithZone:zone]; }); return instance; } + (instancetype)sharePerson { return [self new]; }  快速实现单例 封装到头文件
// 创建单例, 禁用 alloc, new, copy, mutableCopy 方法 #define SINGLETON_DEF(_type_) + (_type_ *)sharedInstance;\ +(instancetype) alloc __attribute__((unavailable(&quot;call sharedInstance instead&quot;)));\ +(instancetype) new __attribute__((unavailable(&quot;call sharedInstance instead&quot;)));\ -(instancetype) copy __attribute__((unavailable(&quot;call sharedInstance instead&quot;)));\ -(instancetype) mutableCopy __attribute__((unavailable(&quot;call sharedInstance instead&quot;)));\ // 实现单例 #define SINGLETON_IMP(_type_) + (_type_ *)sharedInstance {\ static _type_ *sharedInstance = nil;\ static dispatch_once_t onceToken;\ dispatch_once(&amp;onceToken, ^{\ sharedInstance = [[super alloc] init];\ });\ return sharedInstance;\ }  声明
